import React , {useState} from 'react';

function App() {

  const [headindText, setHeadingText] = useState('Welcome');

  const [isMouseOver, setMouseOver] = useState(false);

  function handleClick(){
    setHeadingText('Submitted! Thank You!')
  }

  function handleMouseOver(){
    setMouseOver(true)
  }

  function handleMouseOut(){
    setMouseOver(false)
  }

  return (
    <div className="container">
      <h1>{headindText}</h1><br/>
      <input type="text" placeholder="What's your name?" />
      <button
      style = {{backgroundColor:isMouseOver?'yellow':'white'}}
      onClick = {handleClick}
      onMouseOver = {handleMouseOver}
      onMouseOut = {handleMouseOut}
      >
      Submit
      </button><br/><br/>
      <button
      style = {{backgroundColor:isMouseOver?'yellow':'white',width:'50%'}}
      onClick = {handleClick}
      onMouseOver = {handleMouseOver}
      onMouseOut = {handleMouseOut}
      >
      Clear
      </button>
    </div>
  );
}

export default App;
