import React , {useState} from 'react';

function App() {

  const [headindText, setHeadingText] = useState('Welcome')
  return (
    <div className="container">
      <h1>{headindText}</h1>
      <input type="text" placeholder="What's your name?" />
      <button>Submit</button>
    </div>
  );
}

export default App;
